  `title` text CHARACTER SET utf8 COLLATE utf8_bin,
  `ismany` int(11) DEFAULT '0' COMMENT '是否多选',
  `ismain` varchar(11) CHARACTER SET utf8 COLLATE utf8_bin DEFAULT '1' COMMENT '唯一标签，获取一组',
  `remark` varchar(250) CHARACTER SET utf8 COLLATE utf8_bin DEFAULT NULL COMMENT '备注',
  `createtime` timestamp NOT NULL DEFAULT CURRENT_TIMESTAMP,
  `itemid` int(8) DEFAULT '0',
  `status` int(2) DEFAULT '1',
  PRIMARY KEY (`id`)
) ENGINE=MyISAM AUTO_INCREMENT=40 DEFAULT CHARSET=latin1;

-- ----------------------------
-- Records of sp_toupiao_question
