INSERT INTO `sp_posts` VALUES ('161', '1', '', '2016-01-27 10:09:50', '<table width=\"268\" cellspacing=\"1\" cellpadding=\"0\"><tbody><tr class=\"firstRow\" style=\"height: 14px;\"><td width=\"50\" height=\"14\" valign=\"top\" style=\"padding: 7px;\"><p style=\"text-align: center; margin-top: auto; margin-bottom: auto;\"><strong><span style=\"color: blue; font-family: 宋体; font-size: 16px;\">风 &nbsp;级</span></strong></p></td><td width=\"97\" height=\"14\" valign=\"top\" style=\"padding: 7px;\"><p style=\"text-align: center; margin-top: auto; margin-bottom: auto;\"><strong><span style=\"color: blue; font-family: 宋体; font-size: 16px;\">名 &nbsp;称</span></strong></p></td><td width=\"117\" height=\"14\" valign=\"top\" style=\"padding: 7px;\"><p style=\"text-align: center; margin-top: auto; margin-bottom: auto;\"><strong><span style=\"color: blue; font-family: 宋体; font-size: 16px;\">米∕秒</span></strong></p></td></tr><tr><td width=\"50\" valign=\"top\" style=\"padding: 7px;\"><p style=\"text-align: center; margin-top: auto; margin-bottom: auto;\"><strong><span style=\"color: maroon; font-family: System; font-size: 16px;\">0</span></strong></p></td><td width=\"97\" valign=\"top\" style=\"padding: 7px;\"><p style=\"text-align: center; margin-top: auto; margin-bottom: auto;\"><strong><span style=\"color: green; font-family: 宋体; font-size: 16px;\">静 &nbsp;稳</span></strong></p></td><td width=\"117\" valign=\"top\" style=\"padding: 7px;\"><p style=\"text-align: center; margin-top: auto; margin-bottom: auto;\"><strong><span style=\"color: navy; font-family: 宋体; font-size: 16px;\">0</span></strong><strong><span style=\"color: navy; font-family: 宋体; font-size: 16px;\">～0.2</span></strong></p></td></tr><tr><td width=\"50\" valign=\"top\" style=\"padding: 7px;\"><p style=\"text-align: center; margin-top: auto; margin-bottom: auto;\"><strong><span style=\"color: maroon; font-family: System; font-size: 16px;\">1</span></strong></p></td><td width=\"97\" valign=\"top\" style=\"padding: 7p